import React from 'react';

/*Hello is the name of this app, and this is how you go about extending the React.Component class to cretae the component.*/
const Card = ({id, name, email})=> {
/*Compoment requires that you render something.*/
     /*Between the parenthesis is the JSX code that will change the DOM.*/
    /*note the use of className instead of class - class means something else in JSX*/
    /*f1 tc comes from a package called tachyons that can be installed via npm*/
    return (
     <div className='tc bg-light-green dib br3 pa3 na2 grow bw2 shadow-5'>
      <img alt='robots' src={`https://robohash.org/${id}?200x200`}/>
      <div>
          <h1>{name}</h1>
          <p>{email}</p>
        </div>
      </div>
    );
  }

/*must export this App for it to be imported in index.js*/
export default Card;
