//import React, so I can use JSX and Component, so I can create state.
import React, {Component} from 'react';

//import CardList app
import CardList from './CardList';

//import SearchBox app
import SearchBox from './SearchBox';

//import robots array from robots.js file
import {robots} from './robots';

//create App class to extend Component class
//Component requires render for what we'd normally return
//use constructor and super to create state
//add an event handler - onSearchChange -
//add title - use className tachyons to center title
//add SearchBox and CardList apps.  
//CardList passes robots property of {robots} file

class App extends Component {
	constructor(){
		super()
		this.state = {
			robots: robots,
			searchfield: ''	
		}
	}

onSearchChange(event){
	console.log(event);
}

	render(){
		return(
		<div className='tc'>
				<h1>{`Tom's Cat Friends`}</h1>
			<div>
				<SearchBox searchChange={this.onSearchChange}/>
				<CardList robots={robots}/>
				)
			</div>
		</div>
		)
	}
}


//ReactDOM.render(<CardList robots={robots}/>, document.getElementById('root'));





//export App
export default App;
