//import React so I can use JSX
import React from 'react';

//import Card app, so I can pass properties to it
import Card from './Card';

//create CardList function
//uses map to loop through the robots array to create an array that can be returned later in the function
//returns cardArray for rendering in index.js

const CardList = ({robots}) => {
	return (
			<div>
				{
				robots.map((user, i) => {
				return (
				<Card 
					key={this.state.robots[i].id}
					name={this.state.robots[i].name}
					username={this.state.robots[i].username}
					email={this.state.robots[i].email}
					/>
					)
					})
				}		
			</div>
			)
}

//export CardList for use higher up
export default CardList;
