/*import React. I do not also import {Component} because I am creating an arrow function below*/
import React from 'react';

/*import the Card child component*/
import Card from './Card';
/*CardList is the name of this app, and I am creating it as an arrow function.*/
/*The CardList function does this:*/
  /*takes the robots array and send it to the cardArray function*/
    /*the cardArray function does this:*/
      /*uses the map property to loop through the robots array, creates a card array for each user in the array and returns this card array to the CardList function using data from each item in the array*/
  /* then the CardList function returns that array in JSX code which is exported for use in index.js.*/
const CardList = ({robots}) => {
   robots.map((user, i) => {
   return (
     <div>    
         <Card key={robots[i].id} username={robots[i].username} name={robots[i].name} email={robots[i].email}/>
      </div>
      )
      }
      )
}

/*must export this App for it to be imported in index.js*/

export default CardList;
