/* need to include {Component} in here because we will exted the Component class*/
import React, {Component} from 'react';
/*import child app named CardList*/
import CardList from './CardList';
/*import child app named SearchBox*/
import SearchBox from './SearchBox';
//robots.js is a file that contains a data array - also called robots - which I will use as data. I have to import the robots array it in curly brackets because it is not the default.
import {robots} from './robots';



/*class App extends Component because we need Component to utilize state*/
/*create state object that includes robots and searchfield because the state of these properties are dependent on each other and will change*/
/*remember that Component renders something*/
class App extends Component {
	constructor(){
		super()
		this.state = {
			robots: robots,
			searchfield: ''
		}
	}

	/*creating an event function called onSearch change that takes an event and logs it to the console*/
	/*event.target.value gives us the actual vale of the search box*/
	onSearchChange= (event) => {
		this.setState({searchfield: event.target.value});
		const filteredRobots = this.state.robots.filter(robot =>{
			return robot.name.toLowerCase().includes(this.state.searchfield.toLowerCase());
		})
		console.log(filteredRobots);
	}


	render() {
	return(
		/*add title*/
		/*return SearchBox component*/
		/*return CardList component*/
		<div className = 'tc'>
			<h1>Robofriends</h1>
			<SearchBox searchChange={this.onSearchChange}/>
			<CardList robots={this.state.robots}/>
		</div>
		);
	}
}

export default App;
