//import React to allow working with JSX//
import React from 'react';

//Create a Card component that takes properties as parameters and returns a card with a unique image, a name, and an email name//
const Card = ({id, name, username, email}) => {
	//the information after className comes from the tachyons file imported in a parent
	return (
		<div className='tc bg-light-green dib br3 pa3 na2 grow bw2 shadow-5'>
			<img alt='robots' src={`https://robohash.org/${username}?200x200`}/>
			<div>
				<h1>{name}</h1>
				<p>{email}></p>
			</div>
		</div>
		)
}

//export the card for rendering elsewhere.
export default Card;
