//React library allows JSX
import React from 'react';
//ReactDom allows rendering to a browser
import ReactDOM from 'react-dom';
//import Card component
import Card from './Card';

//import CardList component

//robots.js is a file that contains a data array - also called robots - which I will use as data. I have to import the robots array it in curly brackets because it is not the default.
import {robots} from './robots';

//tachyons is a library I need to install and import - it allows for shortcuts in element formatting - I use it in the Card.js file.
import 'tachyons';

import * as serviceWorker from './serviceWorker';

//rendering Cards (see Card.js) from the CardList component (see CardList.js) and assigning the property {robots}
ReactDOM.render(
	<div>
		<Card id={robots.id[0]} name={robots.name[0]} email={robots.email[0]}/>
	</div>

	, document.getElementById('root'));

// If you want your app to work offline and load faster, you can change
// unregister() to register() below. Note this comes with some pitfalls.
// Learn more about service workers: http://bit.ly/CRA-PWA
serviceWorker.unregister();
