import React, {Component} from 'react';
import CardList from './CardList';
import SearchBox from './SearchBox';

//import {robots} from robots.js as data
import {robots} from './robots';

/*This app component has two states - the robots currently in the list and the contents of the search field.
/*This app, being the parent app, owns these two states*/
/*Any component that is affected by state - in this case, SearchBox and CardList - will need to use this.state.
/*The constructor function is required to to create this.state.*/

class App extends Component {
	constructor(){
		super()
		this.state = {
			robots:robots,
			searchfield:'t'
		}
	}

/*this is an event passed down to SearchBox*/
onSearchChange = (event) => {
	console.log(event)
	this.setState({searchfield:event.target.value});
}


	render(){
	const filteredRobots = this.state.robots.filter(robots =>{
		 return robots.name.toLowerCase().includes(this.state.searchfield.toLowerCase());
		})
	return (
		<div className='tc'>
			<h1>{`Tom's Cat Friends`}</h1>
			<div>
				<SearchBox searchChange={this.onSearchChange}/>
				<CardList robots = {filteredRobots}/>
			</div>
		</div>
		);
}
}


export default App;
