//Using React to allow for JSX.  Not importing {Component} from react, like I normally might do because I am create Card as an arrow function.
import React from 'react';
/*the parameters are destructured properties*/
const Card = ({id, name, email}) => {
/*Between the parenthesis is the JSX code that will change the DOM.*/
    /*note the use of className instead of class - class means something else in JSX*/
    /*the syntax used with className comes from a package called tachyons that can be installed via npm and is imported into index.js*/
    return (
            <div>
              <img alt='robots' src={`https://robohash.org/${id}200x200`}/>
              <div>
                <h1>{name}</h1>
                <p>{email}</p>
              </div>
            </div>
                );   
}
/*must export this App for it to be imported in index.js*/
export default Card;
