/*import React. I do not also import {Component} because I am creating an arrow function below*/
import React from 'react';
/*import the Card child component*/
import Card from './Card';
//robots.js is a file that contains a data array - also called robots - which I will use as data. I have to import the robots array it in curly brackets because it is not the default.
//import {robots} from './robots';

/*CardList is the name of this app, and I am creating it as an arrow function.*/
const CardList = ({robots}) => {
   const cardArray = robots.map((user, i) => {
    return <Card id={robots[i].id} name={robots[i].name} email={robots[i].email} />
    /*Between the parenthesis is the JSX code that will change the DOM.*/
    })
    return (
    <div>
      {cardArray}
    </div>
    );
  }


/*must export this App for it to be imported in index.js*/
export default CardList;
